mapper.select:

select * from (
select ec.id,ec.name as 'json',ec.orderNo,ec.parentId,ec.create_time,ec.type,ec.ep_type,'text' as 'param' from tb_escalator_config ec where ec.ep_type='0' union
select ec.id,ep.param as 'json',ec.orderNo,ec.parentId,ec.create_time,ec.type,ec.ep_type,ep.type as 'param' from tb_escalator_config ec left join tb_elev_param ep on ep.id=ec.id where ec.ep_type='1'
)e where e.type=#{type}

select * from (
select ec.id,ec.name as 'json',ec.orderNo,ec.parentId,ec.create_time,ec.type,ec.ep_type,'text' as 'param' from tb_escalator_config ec where ec.ep_type='0' union
select ec.id,ep.param as 'json',ec.orderNo,ec.parentId,ec.create_time,ec.type,ec.ep_type,ep.type as 'param' from tb_escalator_config ec left join tb_elev_param ep on ep.id=ec.id where ec.ep_type='1'
)e where e.parentId=#{id}

