mapper.select:
SELECT eo.*, customer.customer_name, elev.num, FORMAT(eo.total,'000,000.00') as total, COUNT(IF(elev.flag='1',flag,NULL)) AS WNum, COUNT(IF(elev.flag='2',flag,NULL)) AS YNum, u.NAME AS USERNAME FROM tb_iteminoffer AS eo LEFT JOIN ( SELECT customer_no, customer_name FROM tb_customer_core UNION SELECT customer_no, customer_name FROM tb_customer_merchant UNION SELECT customer_no, customer_name FROM tb_customer_ordinary ) AS customer ON eo.customer_no = customer.customer_no LEFT JOIN ( SELECT *, COUNT(id) AS num FROM tb_Temp_elevator_details GROUP BY tb_Temp_elevator_details.item_id,tb_Temp_elevator_details.offer_version ) AS elev ON eo.item_id = elev.item_id and elev.offer_version = eo.offer_version LEFT JOIN sys_user AS u ON eo.offer_user = u.USER_ID where eo.offer_id=#{offer_id}
and eo.offer_user in
${user_id}

