mapper.select:
select distinct eoffer.*,RES.NAME_ as task_name,RES.ID_ as task_id,(case when RES.ASSIGNEE_ is not null then 1 else 0 end) as type,eoffer.offer_date as TASK_TIME,'1' as TASK_TYPE   from ACT_RU_TASK RES
left join ACT_RU_IDENTITYLINK I on I.TASK_ID_ = RES.ID_ left join act_re_procdef proc on proc.ID_ = RES.PROC_DEF_ID_ 
left join act_ru_execution exe on RES.PROC_INST_ID_=exe.PROC_INST_ID_ 
right join (
	SELECT a.*,u.`NAME` as user_name
	FROM tb_iteminoffer a
	LEFT JOIN sys_user u ON a.offer_user=u.USER_ID


			and a.offer_user in

				${userId}



) eoffer on eoffer.offer_id = substring_index(exe.BUSINESS_KEY_,'.', -1)
WHERE (
  RES.ASSIGNEE_ = #{user_id}
  or (
	RES.ASSIGNEE_ is null
	and (
	  I.USER_ID_ = #{user_id}
	  or
	  I.GROUP_ID_ IN (
		select g.GROUP_ID_ from ACT_ID_MEMBERSHIP g where g.USER_ID_ = #{user_id}
	  )
	)
  )
) and proc.KEY_ = 'offer_changguihuoti' and exe.BUSINESS_KEY_  like 'tb_e_offer.offer_id.%' 

	and eoffer.item_name like CONCAT('%',#{project_name},'%')

order by RES.CREATE_TIME_ desc

	limit #{firstResult},#{maxResults}

SELECT * FROM tb_Temp_elevator_details WHERE id in

	#{item}


	and flag = #{flag}

