mapper.select:
select houses_name
from tb_houses
where houses_name = #{houses_name}

select houses_address
from tb_houses
where houses_address = #{houses_address}

SELECT customer_id, company_address, company_phone, company_email, company_fax, customer_contact, contact_phone
FROM tb_customer_ordinary
where customer_id = #{customer_id}

SELECT id
from tb_houses_type
WHERE type_name = #{houses_type}

SELECT customer_id
FROM tb_customer_ordinary
WHERE customer_name = #{customer_name}

SELECT id
FROM tb_houses_status
where status_name = #{status_name}

SELECT lpr.id as lprId, lci.id as lciId, lco.id as lcoId
from tb_loc_province as lpr
         LEFT JOIN tb_loc_city as lci ON lpr.id = lci.province_id
         LEFT JOIN tb_loc_county as lco ON lci.id = lco.city_id
WHERE lpr.name = #{province_name}
  AND lci.name = #{city_name}
  AND lco.name = #{county_name}
